CREATE TABLE `quizto`.`caper1` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `registration_no` VARCHAR(50) NOT NULL , `first_name` VARCHAR(100) NOT NULL , `last_name` VARCHAR(100) NOT NULL , `provinsi` VARCHAR(100) NOT NULL , `kabkota` VARCHAR(100) NOT NULL , `kampus` VARCHAR(100) NOT NULL , `jurusan` VARCHAR(100) NOT NULL , `alamat` VARCHAR(150) NOT NULL , `telp` VARCHAR(25) NOT NULL , `email` VARCHAR(150) NOT NULL , `password` VARCHAR(150) NOT NULL , `status` ENUM('0','10','20','30') NOT NULL DEFAULT '0' , PRIMARY KEY (`id`), UNIQUE (`registration_no`)) ENGINE = MyISAM;